mapper.select:
SELECT n.id,n.title,n.`message`,n.`publishDate`,n.`state`,u.`nickname`,n.uid,n.`news` FROM notice AS n,USER AS
u WHERE n.`uid`=u.`id`

    and n.uid=#{uid}


    and n.state=#{state}


    and n.state=1


    AND message LIKE concat('%',#{keywords},'%')

ORDER BY n.publishDate DESC limit #{start},#{count};

SELECT n.id,n.`message`,n.`publishDate`,n.`state`,u.`nickname`,n.uid,n.`news` FROM notice AS n,USER AS u WHERE n.`uid`=u.`id` AND n.state=1
AND message LIKE concat('%',#{keywords},'%')
ORDER BY n.publishDate DESC limit #{start},#{count};

SELECT count(*) FROM notice


        AND state=#{state}


        AND uid=#{uid}


        AND message LIKE concat('%',#{keywords},'%')
